import React, { useState } from 'react';

const App = () => {
  const [aaa, setAaa] = useState(false)
  const useConfirm = (message = "", callback, rejection) =>{
      if(typeof callback !== "function"){
        return;
      }
      const confirmAction = () =>{
            if(window.confirm(message)){

              callback();

            }else{
              rejection()
            }
      }
      return confirmAction;
  }


  const deleteWorld = (e) =>{

    console.log("지우다")
    setAaa(true);

  }

  const abort = () =>{
    console.log("취소됨");
  }

  const conDelete = useConfirm("진짜지워?",deleteWorld,abort)
  return (
    <div>
      <button onClick={conDelete}>지우기</button>
      {aaa === true ? <h1>지웠습니다</h1> : null}

    </div>
  );
};

export default App;
