import React, { useState } from 'react';



const useScroll = () =>{
  const [state, setState] = useState({
    x: 0,
    y: 0
  });
}


const App = () => {
  const {y} = useScroll();

  return (
    <div>
      <h1 style = {{position:"fixed",color: y > 100 ? "red" : "blue" }}>스크롤 탐지</h1>
    </div>
  );
};

export default App;
