import React, { useRef, useState } from 'react';


const useFullscreen = (callback) =>{
  const element = useRef()
  const triggerFull = () => {
      if(element.current){
        element.current.requestFullscreen();

        if(callback && typeof callback === "function"){
          callback(true)
        }
      }
  }
    const exitFull = () =>{
      if(callback === false){
        return;
      }
      document.exitFullscreen();


      if(callback && typeof callback === "function"){
        callback(false)
      }
    }
  return {element, triggerFull, exitFull}
}

const App = () => {
  const [fullCh, setFullCh] = useState(false)
  const onFullS = (isFull) =>{

    console.log(isFull ? "큰화면" : "작은화면")
    setFullCh(isFull)
  }
  const {element, triggerFull, exitFull} = useFullscreen(onFullS);
  return (

    <div>
      <div ref={element}>
      <img src="https://i.ibb.co/R6RwNxx/grape.jpg" alt="grape" width="250"></img>

<button onClick={exitFull}>Exit</button>
</div>

<button onClick={triggerFull}>fullscreen</button>
    </div>
  );
};

export default App;
