import React, { useState } from 'react';

const App = () => {
  const [aaa, setAaa] = useState(true)
  const useConfirm = (message = "", callback) =>{
      if(typeof callback !== "function"){
        return;
      }
      const confirmAction = () =>{
            if(window.confirm(message)){
              callback();
            }
      }
      return confirmAction;
  }


  const deleteWorld = () =>{
    console.log("지우다")
    setAaa(false)
    return(
      <h1>지우고 추가</h1>
    )
  }
  const conDelete = useConfirm("진짜지워?",deleteWorld)
  return (
    <div>
      <button onClick={conDelete}>지우기</button>
      { aaa === true ? null : deleteWorld()}
    </div>
  );
};

export default App;
