  }

  const {enablePreve, disablePrevent} = usePreventLeave();
        //usePreventLeave가 두개의 오브젝트를 따로 반환하는게 아니라, 그 키들을 갖는 하나의 오브젝트를 반환

  return (
    <div>
      <button onClick={enablePreve}>잠금</button>
      <button onClick={disablePrevent}>안잠금</button>
