import React, { useEffect, useRef } from 'react';

const App = () => {

  const useFadeIn = () => {
    const element = useRef();
    useEffect(()=>{
      if(element.current){
        const {current} = element;
        current.style.opacity=1
      }
    },[])
    return {ref:element , style:{opacity:0}};
  }

  const fadeInH1 = useFadeIn();

  return (

    <div>
      <h1 {...fadeInH1}>안녕하세요</h1>
    </div>
  );
};

export default App;
