import React from 'react';

const App = () => {
  const useConfirm = (message = "", collback) =>{
      if(typeof callback !== "function"){
        return;
      }
      const confirmAction = () =>{
            if(window.confirm(message)){
              callback();
            }
      }
      return confirmAction;
  }

  const onClick = () =>{
    console.log("클릭됨")
  }

  return (
    <div>
      <h1 onClick={onClick}>button</h1>
    </div>
  );
};

export default App;
