import React, { useState } from 'react';

const App = () => {
  const [aaa, setAaa] = useState(false)
  const useConfirm = (message = "", callback) =>{
      if(typeof callback !== "function"){
        return;
      }
      const confirmAction = () =>{
            if(window.confirm(message)){
              callback();
            }
      }
      return confirmAction;
  }


  const deleteWorld = () =>{

    console.log("지우다")

    return(
      <div><h1>지웠습니다</h1></div>
    )

  }
  const conDelete = useConfirm("진짜지워?",deleteWorld)
  return (
    <div>
      <button onClick={conDelete}>지우기</button>
      {setAaa === true ? deleteWorld() : null}

    </div>
  );
};

export default App;
