import React, { useState } from "react";

export default function App() {
  const useInput = (inputValue, validator) => {
    const [value, setValue] = useState(inputValue);
    const onChange = (e) => {
      let willUpdate = true;

      if (typeof validator === "function") {
        willUpdate = validator(value);
      }

      if (willUpdate) {
        setValue(e.target.value);
      }
      if(willUpdate === false){
        setValue(value.slice(0, -1))
        alert("글자수 초과")
      }
    };

    const onReset = (e) => {
      console.log(value);
      setValue("Mr.","@");
    };
    return { value, onChange, onReset };
           //리턴 해줘야 name 로 사용가능
  };

  const maxLen = value => {
    return !value.includes("@");
    //return value.length <= 10;
  };

  const name = useInput("Mr.", maxLen);
  const mail = useInput("", maxLen)
                          // 벨류와 체인지가 다 들어있음.
  return (
    <div className="App">
      <h1>Hello </h1>
      <input placeholder="Name" {...name} /><p>@</p>
                            {/* name 안에 value값과 onChange 값이 안에 다있다. */}
      <input placeholder="Mail" {...mail}/>
      <button onClick={name.onReset}>확인 리셋</button>
                {/* name 안에있는 onReset 만 가져옴 */}
    </div>
  );
}
