import React, { useEffect } from 'react';

const App = () => {

  const useBeforeLeave = (onBefore) =>{

    if(typeof onBefore!=="function") {
      return;
    }else{}

    const handle = () =>{
      console.log("leaving")
    }

  useEffect(()=>{
        document.addEventListener("mouseleave", handle)
        return () => document.removeEventListener("mouseleave", handle)
    },[])
  }

  return (
    <div>
      <h1>호버링 구현</h1>
    </div>
  );
};

export default App;
