import React, { useEffect, useState } from 'react';
const useScroll = () =>{

  const [state, setState] = useState({
    x:0,
    y:0
  })


  const onScroll = (e) => {
    console.log(e)
  }

  useEffect(() => {

    window.addEventListener("scroll", onScroll);
    return() => window.removeEventListener("scroll",onScroll)

    }, []);

  return state;
}
const App = () => {
  const {y} = useScroll()
  return (
    <div style={{height:"1000vh"}}>
      {console.log("테스트")}
      <h1 style={{position: 'fixed',color:y > 100 ? "red" : "blue"}}>테스트</h1>
    </div>
  );
};

export default App;
