import React from 'react';


const useNotification = (title, options) =>{
  if (!("Notification" in window)) {
    return;
    }

  const fireNotif = () =>{
    if(Notification.performance !== "granted"){
        Notification.requestPermission().then(Permission =>{
          if(Permission === "granted"){
              new Notification(title,options)
              console.log("눌렸음")
          } else{
            return;
          }
        })

    } else{
      new Notification(title, options)
    }
  }

};

const App = () => {
  const triggerNotif = useNotification("타이틀이 바뀌었어")
  return (
    <div>
        <button onClick={triggerNotif}>안녕하세요</button>
    </div>
  );
};

export default App;
