import React, { useEffect, useRef } from 'react';





const App = () => {



  const useClick = (onClick) =>{
    const element = useRef();
    useEffect(()=>{
      if(element.current){
        element.current.addEventListener("click",onClick);
      }
    },[])
    return;
  }

  const sayHello = () => console.log("안녕하세요")
  const title = useClick(sayHello)

  return (

    <div>
      <h1 ref={title}>hi</h1>

    </div>
  );
};

export default App;
