import React, { useEffect, useRef, useState } from 'react';





const App = () => {


  const useClick = (onClick) =>{
    const element = useRef();
    useEffect(()=>{
      if(typeof onClick !== "function"){
        return element;
      };

      if(element.current){
        element.current.addEventListener("click", onClick)
      }
    },[])
  }


  const sayHello= () =>{
    console.log("안녕이다")
  };
  const title = useClick(sayHello)

  return (

    <div>
      <h1 ref={title}>hi</h1>

    </div>
  );
};

export default App;
