import defaultAxios from "axios";
import { useEffect, useState } from "react";

const useAxios = (opts,axiosInstance = defaultAxios) =>{

    const [state, setState] = useState({
        loading:true,
        error:null,
        data:null

    })

    useEffect(()=>{

    if(!opts.url){
        return;
    }

        return state;
    },[]);

};

export default useAxios;
