const useFullscreen = () =>{
  const element = useRef()
  const triggerFull = () => {
      if(element.current){
        element.current.requestFullScreen();
      }
  }
  return element
}

const App = () => {
  const fullScrEl = useFullscreen();
  return (

    <div>
      <img ref={fullScrEl} img src="https://i.ibb.co/R6RwNxx/grape.jpg" alt="grape" width="250"
></img>
<button>fullscreen</button>
    </div>
  );
};

export default App;
