import React, { useState } from 'react';


const content = [
  {
    tab:"Section 1",
    content: "섹션 1이야"
  },
  {
    tab:"Section 2",
    content: "섹션 2이야"
  },
]


const useTabs = (soloTab, allTabs) =>{
  const [currentIndex, setCurrentIndex] = useState(soloTab)
  const {currentItem} = useTabs(0, content)

  if(!allTabs || Array.isArray(allTabs)){
    return (
      alert("배열이 아니다")
    )
  }


  return {
    currentItem: allTabs[currentIndex]
  }
}

const App = () => {

  return (
    <div>
      {content.map(section => <button>{section.tab}</button>)}
      <h1>{currentItem.content}</h1>
    </div>
  );
};

export default App;
