import React, { useRef,useEffect, useState } from 'react';
const useScroll = () =>{
  console.log("시작됨")
  const [state, setState] = useState({
    x:0,
    y:0
  })


  const onScroll = (e) => {
    console.log(e)
  }

  useEffect(() => {
    console.log("유스이펙트")
    window.addEventListener("scroll", onScroll);

    }, []);

  return state;
}
const App = () => {

  const {y} = useScroll;



  return (
    <div style={{height:"1000vh"}}>
      <h1 style={{position:'fixed',color: y > 100 ? "red":"blue" }}>
        스크롤 탐지</h1>
    </div>
  );
};

export default App;
