import React, { useState } from 'react';
import Onom from './component/Onom';
import Toww from './component/Toww';


const content = [
  { //[0]
    button:"Section 1",
    page: <Onom/>,
    color:"blue"
    
  },
  {
    button:"Section 2",
    page: <Toww/>,
    color:"#239475"

  }
]

const App = () => {

  const useTabs = (initialTab, allTabs) => {
    const [currentIndex, setCurrentIndex] = useState(initialTab)


    //배열 확인 조건문(오류확인)
    if(!allTabs || !Array.isArray(allTabs)){
      return(
        alert("배열이아님")
      )
    }
   
    // 컨텐트 리턴
    return{
      currentItem: allTabs[currentIndex],
      changeItem: setCurrentIndex,
   
      
    }
    
  }
  
  const {currentItem, changeItem} = useTabs(0, content);
  return (
    <div>
      {content.map((section, index, abc) => <button key={index} onClick={() => changeItem(index)}>{section.button}</button>)}
      
      <h1 style={{backgroundColor:`${currentItem.color}`,borderRadius:'10px'}}>
       {currentItem.page}</h1>
    </div>
  );
};

export default App;